#reduce n to get rid of leading digits
def reduce(n):
    while(n > 1):
        if(n%2==0):
            n/=2
        elif(n%5==0):
            n/=5
        else:
            return n
    return 1
#find the length of the long division cycle for n
def longDivision (n):
    count = 0
    numerator = 1
    n = reduce(n)
    while numerator < n:
        numerator *= 10
    original = numerator % n
    while(numerator != original and numerator != 0):
        numerator *= 10
        numerator = numerator % n
        count += 1
    return count
